import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../context/ThemeContext';

interface ChatResponse {
    id: number;
    message: string;
    response: string;
    created_at: string;
}

const Chat: React.FC = () => {
    const [message, setMessage] = useState('');
    const [chats, setChats] = useState<ChatResponse[]>([]);
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const { theme, toggleTheme } = useTheme();
    const chatContainerRef = useRef<HTMLDivElement>(null);

    const fetchChats = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('No authentication token found');
            }

            const response = await fetch('http://localhost:8000/api/chats', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                throw new Error('Failed to fetch chats');
            }

            const data = await response.json();
            setChats(data);
        } catch (error) {
            console.error('Error fetching chats:', error);
        }
    };

    const sendMessage = async () => {
        if (!message.trim()) return;

        setIsLoading(true);
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('No authentication token found');
            }

            const response = await fetch('http://localhost:8000/api/chat', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ message }),
            });

            if (!response.ok) {
                throw new Error('Failed to send message');
            }

            const data = await response.json();
            setChats([...chats, data]);
            setMessage('');
        } catch (error) {
            console.error('Error sending message:', error);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchChats();
    }, []);

    useEffect(() => {
        if (chatContainerRef.current) {
            chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
        }
    }, [chats]);

    return (
        <div className={`min-h-screen transition-colors duration-300 ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
            <div className="container mx-auto px-4 py-8">
                <div className="flex justify-between items-center mb-6">
                    <h1 className="text-3xl font-bold">Chat</h1>
                    <button
                        onClick={toggleTheme}
                        className={`p-2 rounded-full transition-colors duration-300 ${
                            theme === 'dark' ? 'bg-gray-700 hover:bg-gray-600' : 'bg-gray-200 hover:bg-gray-300'
                        }`}
                    >
                        {theme === 'dark' ? 'ðŸŒž' : 'ðŸŒ™'}
                    </button>
                </div>

                <div className={`rounded-lg shadow-lg overflow-hidden ${
                    theme === 'dark' ? 'bg-gray-800' : 'bg-white'
                }`}>
                    <div 
                        ref={chatContainerRef}
                        className="h-[calc(100vh-200px)] overflow-y-auto p-4"
                    >
                        {error && (
                            <div className="text-red-500 text-center p-2">{error}</div>
                        )}
                        {isLoading && (
                            <div className="flex justify-center">
                                <div className="animate-spin rounded-full h-8 w-8 border-4 border-blue-500 border-t-transparent"></div>
                            </div>
                        )}
                        {chats.map((chat) => (
                            <div key={chat.id} className="space-y-2">
                                <div className={`p-3 rounded-lg ${
                                    theme === 'dark' ? 'bg-gray-700' : 'bg-blue-100'
                                }`}>
                                    <p className="font-semibold">You:</p>
                                    <p>{chat.message}</p>
                                </div>
                                <div className={`p-3 rounded-lg ${
                                    theme === 'dark' ? 'bg-gray-600' : 'bg-gray-100'
                                }`}>
                                    <p className="font-semibold">AI:</p>
                                    <p>{chat.response}</p>
                                </div>
                            </div>
                        ))}
                    </div>

                    <form onSubmit={(e) => { e.preventDefault(); sendMessage(); }} className="p-4 border-t border-gray-200">
                        <div className="flex space-x-4">
                            <input
                                type="text"
                                value={message}
                                onChange={(e) => setMessage(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                                className={`flex-1 px-4 py-2 rounded-lg border focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                                    theme === 'dark' 
                                        ? 'bg-gray-700 border-gray-600 text-white' 
                                        : 'bg-white border-gray-300 text-gray-900'
                                }`}
                                placeholder="Type your message..."
                            />
                            <button
                                type="submit"
                                disabled={isLoading}
                                className={`px-4 py-2 rounded-lg text-white ${
                                    isLoading
                                        ? 'bg-gray-400 cursor-not-allowed'
                                        : theme === 'dark'
                                            ? 'bg-blue-600 hover:bg-blue-700'
                                            : 'bg-blue-500 hover:bg-blue-600'
                                }`}
                            >
                                {isLoading ? 'Sending...' : 'Send'}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Chat;
