import React, { useState, useEffect } from 'react';
import { sendMessage, getChatHistory, ChatResponse } from '../services/chatService';

const Chat: React.FC = () => {
    const [message, setMessage] = useState('');
    const [chatHistory, setChatHistory] = useState<ChatResponse[]>([]);
    const [error, setError] = useState('');

    useEffect(() => {
        loadChatHistory();
    }, []);

    const loadChatHistory = async () => {
        try {
            const history = await getChatHistory();
            setChatHistory(history);
        } catch (error) {
            setError('Failed to load chat history');
        }
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!message.trim()) return;

        try {
            const response = await sendMessage(message);
            setChatHistory([...chatHistory, response]);
            setMessage('');
        } catch (error) {
            setError('Failed to send message');
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
            <div className="relative py-3 sm:max-w-xl sm:mx-auto">
                <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
                    <div className="max-w-md mx-auto">
                        <div className="divide-y divide-gray-200">
                            <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                                {error && (
                                    <div className="text-red-500 text-center">{error}</div>
                                )}
                                {chatHistory.map((chat) => (
                                    <div key={chat.chat_id} className="space-y-4">
                                        <div className="bg-blue-100 p-4 rounded-lg">
                                            <p className="font-semibold">You:</p>
                                            <p>{chat.message}</p>
                                        </div>
                                        <div className="bg-gray-100 p-4 rounded-lg">
                                            <p className="font-semibold">AI:</p>
                                            <p>{chat.response}</p>
                                        </div>
                                    </div>
                                ))}
                            </div>
                            <form onSubmit={handleSubmit} className="pt-6 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                                <div className="flex items-center space-x-4">
                                    <input
                                        type="text"
                                        value={message}
                                        onChange={(e) => setMessage(e.target.value)}
                                        className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                        placeholder="Type your message..."
                                    />
                                    <button
                                        type="submit"
                                        className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                                    >
                                        Send
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Chat;
