import React, { useState, useEffect, useRef } from 'react';
import { sendMessage, getChatHistory, ChatResponse } from '../services/chatService';
import { useTheme } from '../context/ThemeContext';

const Chat: React.FC = () => {
    const [message, setMessage] = useState('');
    const [chatHistory, setChatHistory] = useState<ChatResponse[]>([]);
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const { theme, toggleTheme } = useTheme();
    const chatContainerRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        loadChatHistory();
    }, []);

    useEffect(() => {
        if (chatContainerRef.current) {
            chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
        }
    }, [chatHistory]);

    const loadChatHistory = async () => {
        try {
            setIsLoading(true);
            const history = await getChatHistory();
            setChatHistory(history);
        } catch (error) {
            setError('Failed to load chat history');
        } finally {
            setIsLoading(false);
        }
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!message.trim()) return;

        try {
            setIsLoading(true);
            setError('');
            const response = await sendMessage(message);
            setChatHistory([...chatHistory, response]);
            setMessage('');
        } catch (error) {
            setError('Failed to send message');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className={`min-h-screen transition-colors duration-300 ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
            <div className="container mx-auto px-4 py-8">
                <div className="flex justify-between items-center mb-6">
                    <h1 className="text-2xl font-bold">AI Chat</h1>
                    <button
                        onClick={toggleTheme}
                        className={`p-2 rounded-full transition-colors duration-300 ${
                            theme === 'dark' ? 'bg-gray-700 hover:bg-gray-600' : 'bg-gray-200 hover:bg-gray-300'
                        }`}
                    >
                        {theme === 'dark' ? 'ðŸŒž' : 'ðŸŒ™'}
                    </button>
                </div>

                <div className={`rounded-lg shadow-lg overflow-hidden ${
                    theme === 'dark' ? 'bg-gray-800' : 'bg-white'
                }`}>
                    <div 
                        ref={chatContainerRef}
                        className="h-[60vh] overflow-y-auto p-4 space-y-4"
                    >
                        {error && (
                            <div className="text-red-500 text-center p-2">{error}</div>
                        )}
                        {isLoading && (
                            <div className="flex justify-center">
                                <div className="animate-spin rounded-full h-8 w-8 border-4 border-blue-500 border-t-transparent"></div>
                            </div>
                        )}
                        {chatHistory.map((chat) => (
                            <div key={chat.chat_id} className="space-y-2">
                                <div className={`p-3 rounded-lg ${
                                    theme === 'dark' ? 'bg-gray-700' : 'bg-blue-100'
                                }`}>
                                    <p className="font-semibold">You:</p>
                                    <p>{chat.message}</p>
                                </div>
                                <div className={`p-3 rounded-lg ${
                                    theme === 'dark' ? 'bg-gray-600' : 'bg-gray-100'
                                }`}>
                                    <p className="font-semibold">AI:</p>
                                    <p className="whitespace-pre-wrap">{chat.response}</p>
                                </div>
                            </div>
                        ))}
                    </div>

                    <form onSubmit={handleSubmit} className="p-4 border-t border-gray-200">
                        <div className="flex space-x-4">
                            <input
                                type="text"
                                value={message}
                                onChange={(e) => setMessage(e.target.value)}
                                className={`flex-1 px-4 py-2 rounded-lg border focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                                    theme === 'dark' 
                                        ? 'bg-gray-700 border-gray-600 text-white' 
                                        : 'bg-white border-gray-300 text-gray-900'
                                }`}
                                placeholder="Type your message..."
                                disabled={isLoading}
                            />
                            <button
                                type="submit"
                                className={`px-6 py-2 rounded-lg font-semibold transition-colors duration-300 ${
                                    isLoading
                                        ? 'bg-gray-400 cursor-not-allowed'
                                        : theme === 'dark'
                                            ? 'bg-blue-600 hover:bg-blue-700'
                                            : 'bg-blue-500 hover:bg-blue-600'
                                } text-white`}
                                disabled={isLoading}
                            >
                                {isLoading ? 'Sending...' : 'Send'}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Chat;
